mapper.select:
		select `id`,`parent_dept_id`,`dept_name`,`dept_number`,`dept_grade`,`dept_inform`,`dept_weight`,`order_weight`,`create_user`,`create_time`,`update_user`,`update_time`,`delete_user`,`delete_time`,`del_flag` from base_dept

		  		   and id = #{id} 
		  		   and parent_dept_id = #{parentDeptId} 
		  		   and dept_name = #{deptName} 
		  		   and dept_number = #{deptNumber} 
		  		   and dept_grade = #{deptGrade} 
		  		   and dept_inform = #{deptInform} 
		  		   and dept_weight = #{deptWeight} 
		  		   and order_weight = #{orderWeight} 
		  		   and create_user = #{createUser} 
		  		   and create_time = #{createTime} 
		  		   and update_user = #{updateUser} 
		  		   and update_time = #{updateTime} 
		  		   and delete_user = #{deleteUser} 
		  		   and delete_time = #{deleteTime} 
		  		  and del_flag = '0'



                order by ${sort} ${order}


                order by order_weight



			limit #{offset}, #{limit}

select count(*) from base_dept

  		   and id = #{id} 
  		   and parent_dept_id = #{parentDeptId} 
  		   and dept_name = #{deptName} 
  		   and dept_number = #{deptNumber} 
  		   and dept_grade = #{deptGrade} 
  		   and dept_inform = #{deptInform} 
  		   and dept_weight = #{deptWeight} 
  		   and order_weight = #{orderWeight} 
  		   and create_user = #{createUser} 
  		   and create_time = #{createTime} 
  		   and update_user = #{updateUser} 
  		   and update_time = #{updateTime} 
  		   and delete_user = #{deleteUser} 
  		   and delete_time = #{deleteTime} 
  		  and del_flag = '0'

select `id`,`parent_dept_id`,`dept_name`,dept_number,`dept_grade`,`dept_inform`,`dept_weight`,`order_weight`,`create_user`,`create_time`,`update_user`,`update_time`,`delete_user`,`delete_time`,`del_flag` from base_dept

	 and id = #{id} 
	 and parent_dept_id = #{parentDeptId} 
	 and dept_name = #{deptName} 
	 and dept_number = #{deptNumber} 
	and (dept_grade = '1' or dept_grade = '0')
	 and dept_inform = #{deptInform} 
	 and dept_weight = #{deptWeight} 
	 and order_weight = #{orderWeight} 
	 and create_user = #{createUser} 
	 and create_time = #{createTime} 
	 and update_user = #{updateUser} 
	 and update_time = #{updateTime} 
	 and delete_user = #{deleteUser} 
	 and delete_time = #{deleteTime} 
	and del_flag ='0'



		order by ${sort} ${order}


		order by order_weight



	limit #{offset}, #{limit}
