mapper.select:
		select `id`,`parent_id`,`scale_title`,`scale_score`,`scale_type`,`scale_weight`,`order_weight`,`create_user`,`create_time`,`delete_user`,`delete_time`,`del_flag` from scale_digital_rectal

		  		   and id = #{id} 
		  		   and parent_id = #{parentId} 
		  		   and scale_title = #{scaleTitle} 
		  		   and scale_score = #{scaleScore} 
		  		   and scale_type = #{scaleType} 
		  		   and scale_weight = #{scaleWeight} 
		  		   and order_weight = #{orderWeight} 
		  		   and create_user = #{createUser} 
		  		   and create_time = #{createTime} 
		  		   and delete_user = #{deleteUser} 
		  		   and delete_time = #{deleteTime} 
			and del_flag = '0'



                order by ${sort} ${order}


                order by order_weight



			limit #{offset}, #{limit}

