mapper.select:
		select  from base_anamnesis_eating_drug

		  		   and id = #{id} 
			 and eating_drug_name LIKE CONCAT(CONCAT('%', #{eatingDrugName}), '%')


		  		   and spell_name = #{spellName} 
		  		   and eating_drug_number = #{eatingDrugNumber} 
		  		   and order_weight = #{orderWeight} 
		  		   and create_name = #{createName} 
		  		   and create_time = #{createTime} 
		  		   and update_name = #{updateName} 
		  		   and update_time = #{updateTime} 
		  		   and delete_name = #{deleteName} 
		  		   and delete_time = #{deleteTime} 
		  		  and del_flag = '0'






                order by order_weight ASC



			limit #{offset}, #{limit}

select count(*) from base_anamnesis_eating_drug

  		   and id = #{id} 
	  and eating_drug_name LIKE CONCAT(CONCAT('%', #{eatingDrugName}), '%')


  		   and spell_name = #{spellName} 
  		   and eating_drug_number = #{eatingDrugNumber} 
  		   and order_weight = #{orderWeight} 
  		   and create_name = #{createName} 
  		   and create_time = #{createTime} 
  		   and update_name = #{updateName} 
  		   and update_time = #{updateTime} 
  		   and delete_name = #{deleteName} 
  		   and delete_time = #{deleteTime} 
  		   and del_flag = '0'
