mapper.select:
		select  from base_anamnesis_illness

		  		   and id = #{id} 
		  		   and anamnesis_illness_name  LIKE CONCAT(CONCAT('%', #{anamnesisIllnessName}), '%')  
		  		   and spell_name = #{spellName} 
		  		   and anamnesis_illness_number = #{anamnesisIllnessNumber} 
		  		   and order_weight = #{orderWeight} 
		  		   and create_user = #{createUser} 
		  		   and create_time = #{createTime} 
		  		   and update_user = #{updateUser} 
		  		   and update_time = #{updateTime} 
		  		   and delete_user = #{deleteUser} 
		  		   and delete_time = #{deleteTime} 
		  		  and del_flag ='0'



                order by order_weight


                order by order_weight asc



			limit #{offset}, #{limit}

