select count(*) from base_city

	 and id = #{id} 
	 and parent_city_id = #{parentCityId} 
	 and city_name = #{cityName} 
	 and city_type = #{cityType} 
	 and order_weight = #{orderWeight} 
	 and create_user = #{createUser} 
	 and create_time = #{createTime} 
	 and city_weight = #{cityWeight} 
	 and update_user = #{updateUser} 
	 and update_time = #{updateTime} 
	 and delete_user = #{deleteUser} 
	 and delete_time = #{deleteTime} 
	and del_flag = '0'

select  from base_city

	 and id = #{id} 
	 and parent_city_id = #{parentCityId} 
	 and city_name = #{cityName} 
	and (city_type = '1' or city_type = '2'or city_type = '0')
	 and order_weight = #{orderWeight} 
	 and create_user = #{createUser} 
	 and create_time = #{createTime} 
	 and city_weight = #{cityWeight} 
	 and update_user = #{updateUser} 
	 and update_time = #{updateTime} 
	 and delete_user = #{deleteUser} 
	 and delete_time = #{deleteTime} 
	AND  del_flag = '0'



		order by order_weight


		order by order_weight desc



	limit #{offset}, #{limit}
