mapper.select:
		select  from base_education

		  		   and id = #{id} 
			 and education_name LIKE CONCAT(CONCAT('%', #{educationName}), '%')


		  		   and education_number = #{educationNumber} 
		  		   and order_weight = #{orderWeight} 
		  		   and create_user = #{createUser} 
		  		   and create_time = #{createTime} 
		  		   and update_user = #{updateUser} 
		  		   and update_time = #{updateTime} 
		  		   and delete_user = #{deleteUser} 
		  		   and delete_time = #{deleteTime} 
		  		  and del_flag = '0'



                order by ${sort} ${order}


                order by order_weight



			limit #{offset}, #{limit}

select count(*) from base_education

  		   and id = #{id} 
	  and education_name LIKE CONCAT(CONCAT('%', #{educationName}), '%')
  		   and education_number = #{educationNumber} 
  		   and order_weight = #{orderWeight} 
  		   and create_user = #{createUser} 
  		   and create_time = #{createTime} 
  		   and update_user = #{updateUser} 
  		   and update_time = #{updateTime} 
  		   and delete_user = #{deleteUser} 
  		   and delete_time = #{deleteTime} 
  		   and del_flag = '0'
