mapper.select:
		select  from base_ipss

		  		   and id = #{id} 
		  		   and parent_id = #{parentId} 
		  		   and ipss_title = #{ipssTitle} 
		  		   and ipss_score = #{ipssScore} 
		  		   and ipss_type = #{ipssType} 
		  		   and ipss_weight = #{ipssWeight} 
		  		   and order_weight = #{orderWeight} 
		  		   and create_user = #{createUser} 
		  		   and create_time = #{createTime} 
		  		   and delete_user = #{deleteUser} 
		  		   and delete_time = #{deleteTime} 
				AND del_flag='0'



                order by order_weight ASC


                order by order_weight ASC



			limit #{offset}, #{limit}

select  from base_ipss

	 and id = #{id} 
	 and parent_id = #{parentId} 
	 and ipss_title = #{ipssTitle} 
	 and ipss_score = #{ipssScore} 
	and (ipss_type ='1' OR ipss_type ='a' OR ipss_type ='b')
	 and ipss_weight = #{ipssWeight} 
	 and order_weight = #{orderWeight} 
	 and create_user = #{createUser} 
	 and create_time = #{createTime} 
	 and delete_user = #{deleteUser} 
	 and delete_time = #{deleteTime} 
	AND del_flag='0'



		order by order_weight ASC


		order by order_weight ASC



	limit #{offset}, #{limit}

select count(*) from base_ipss

  		   and id = #{id} 
  		   and parent_id = #{parentId} 
  		   and ipss_title = #{ipssTitle} 
  		   and ipss_score = #{ipssScore} 
  		   and ipss_type = #{ipssType} 
  		   and ipss_weight = #{ipssWeight} 
  		   and order_weight = #{orderWeight} 
  		   and create_user = #{createUser} 
  		   and create_time = #{createTime} 
  		   and delete_user = #{deleteUser} 
  		   and delete_time = #{deleteTime} 
	 AND del_flag='0'
