select  from base_nih_cpsi

     and id = #{id} 
     and parent_id = #{parentId} 
     and nih_cpsi_title = #{nihCpsiTitle} 
     and nih_cpsi_score = #{nihCpsiScore} 
    and (nih_cpsi_type='a' OR nih_cpsi_type='c' OR nih_cpsi_type='b' OR nih_cpsi_type='1')
     and nih_cpsi_weight = #{nihCpsiWeight} 
     and order_weight = #{orderWeight} 
     and create_user = #{createUser} 
     and create_time = #{createTime} 
     and delete_user = #{deleteUser} 
     and delete_time = #{deleteTime} 
    AND del_flag='0'



        order by order_weight ASC


        order by order_weight ASC



    limit #{offset}, #{limit}

select count(*) from base_nih_cpsi

     and id = #{id} 
     and parent_id = #{parentId} 
     and nih_cpsi_title = #{nihCpsiTitle} 
     and nih_cpsi_score = #{nihCpsiScore} 
