mapper.select:
		select  from scale_urine_routine

		  		   and id = #{id} 
		  		   and parent_id = #{parentId} 
		  		   and scale_title = #{scaleTitle} 
		  		   and scale_score = #{scaleScore} 
		  		   and scale_type = #{scaleType} 
		  		   and scale_weight = #{scaleWeight} 
		  		   and order_weight = #{orderWeight} 
		  		   and create_user = #{createUser} 
		  		   and create_time = #{createTime} 
		  		   and delete_user = #{deleteUser} 
		  		   and delete_time = #{deleteTime} 
		  		  and del_flag = '0'



                order by scale_weight ASC


                order by scale_weight ASC



			limit #{offset}, #{limit}

