mapper.select:
		select `id`,`doctor_title_number`,`doctor_title_name`,`order_weight`,`create_user`,`create_time`,`update_user`,`update_time`,`delete_user`,`delete_time`,`del_flag` from base_doctor_title

		  		   and id = #{id} 
		  		   and doctor_title_number = #{doctorTitleNumber} 
			 and doctor_title_name LIKE CONCAT(CONCAT('%', #{doctorTitleName}), '%')


		  		   and order_weight = #{orderWeight} 
		  		   and create_user = #{createUser} 
		  		   and create_time = #{createTime} 
		  		   and update_user = #{updateUser} 
		  		   and update_time = #{updateTime} 
		  		   and delete_user = #{deleteUser} 
		  		   and delete_time = #{deleteTime} 
					and del_flag = '0'



                order by ${sort} ${order}


                order by order_weight



			limit #{offset}, #{limit}

select count(*) from base_doctor_title

  		   and id = #{id} 
