mapper.select:
		select `id`,`anamnesis_type_name`,`anamnesis_type_number`,`order_weight`,`create_user`,`create_time`,`update_user`,`update_time` from base_anamnesis_type

		  		   and id = #{id} 
		  		   and anamnesis_type_name = #{anamnesisTypeName} 
		  		   and anamnesis_type_number = #{anamnesisTypeNumber} 
		  		   and order_weight = #{orderWeight} 
		  		   and create_user = #{createUser} 
		  		   and create_time = #{createTime} 
		  		   and update_user = #{updateUser} 
		  		   and update_time = #{updateTime} 


		  		   and del_flag = #{delFlag} 



                order by ${orderWeight}


                order by id desc



			limit #{offset}, #{limit}

