mapper.select:
		select `id`,`hospital_type_name`,`hospital_type_number`,`order_weight`,`create_user`,`create_time`,`update_user`,`update_time` from base_hospital_type

		  		   and id = #{id} 
		  		   and hospital_type_name = #{hospitalTypeName} 
		  		   and hospital_type_number = #{hospitalTypeNumber} 
		  		   and order_weight = #{orderWeight} 
		  		   and create_user = #{createUser} 
		  		   and create_time = #{createTime} 
		  		   and update_user = #{updateUser} 
		  		   and update_time = #{updateTime} 
		  		   and delete_user = #{deleteUser} 
		  		   and delete_time = #{deleteTime} 
			and del_flag = '0'



                order by order_weight


                order by order_weight asc



			limit #{offset}, #{limit}

select count(*) from base_hospital_type

  		   and id = #{id} 
  		   and hospital_type_name = #{hospitalTypeName} 
  		   and hospital_type_number = #{hospitalTypeNumber} 
  		   and order_weight = #{orderWeight} 
  		   and create_user = #{createUser} 
  		   and create_time = #{createTime} 
  		   and update_user = #{updateUser} 
  		   and update_time = #{updateTime} 
  		   and delete_user = #{deleteUser} 
  		   and delete_time = #{deleteTime} 
	 and del_flag = '0'
