mapper.select:
		select from base_blood_group

		  		   and id = #{id} 
			 and blood_group_name LIKE CONCAT(CONCAT('%', #{bloodGroupName}), '%')

		  		   and blood_group_number = #{bloodGroupNumber} 
		  		   and order_weight = #{orderWeight} 
		  		   and create_user = #{createUser} 
		  		   and create_time = #{createTime} 
		  		   and update_user = #{updateUser} 
		  		   and update_time = #{updateTime} 
		  		   and delete_user = #{deleteUser} 
		  		   and delete_time = #{deleteTime} 
		  		  and del_flag = '0'



                order by ${sort} ${order}


                order by order_weight



			limit #{offset}, #{limit}

select count(*) from base_blood_group

  		   and id = #{id} 
	  and blood_group_name LIKE CONCAT(CONCAT('%', #{bloodGroupName}), '%')


  		   and blood_group_number = #{bloodGroupNumber} 
  		   and order_weight = #{orderWeight} 
  		   and create_user = #{createUser} 
  		   and create_time = #{createTime} 
  		   and update_user = #{updateUser} 
  		   and update_time = #{updateTime} 
  		   and delete_user = #{deleteUser} 
  		   and delete_time = #{deleteTime} 
  		  and del_flag = '0'
