mapper.select:
		select  from base_hospital_dept

		  		   and id = #{id} 
		  		   and hospital_id = #{hospitalId} 
		  		   and dept_id = #{deptId} 
		  		   and dept_inform = #{deptInform} 
		  		   and dept_weight = #{deptWeight} 
		  		   and activation_flag = #{activationFlag} 
		  		   and order_weight = #{orderWeight} 
		  		   and create_user = #{createUser} 
		  		   and create_time = #{createTime} 
		  		   and update_user = #{updateUser} 
		  		   and update_time = #{updateTime} 
		  		   and delete_user = #{deleteUser} 
		  		   and delete_time = #{deleteTime} 
		  		   and del_flag = #{delFlag} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

