mapper.select:
		select   from base_hospital

		  		   and id = #{id} 
			 and hospital_name LIKE CONCAT(CONCAT('%', #{hospitalName}), '%')



		  		   and hospital_alias = #{hospitalAlias} 
		  		   and hospital_inform = #{hospitalInform} 
		  		   and hospital_grade = #{hospitalGrade} 
		  		   and hospital_weight = #{hospitalWeight} 
		  		   and type_id = #{typeId} 
		  		   and city_id = #{cityId} 
		  		   and activation_flag = #{activationFlag} 
		  		   and order_weight = #{orderWeight} 
		  		   and create_user = #{createUser} 
		  		   and create_time = #{createTime} 
		  		   and update_user = #{updateUser} 
		  		   and update_time = #{updateTime} 
		  		   and delete_user = #{deleteUser} 
		  		   and delete_time = #{deleteTime} 
		  		  and del_flag = '0'



                order by ${sort} ${order}


                order by order_weight



			limit #{offset}, #{limit}

