mapper.select:
select

from sys_role

	 and role_id = #{roleId} 
	 and role_name = #{roleName} 
	 and role_sign = #{roleSign} 
	 and remark = #{remark} 
	 and user_id_create = #{userIdCreate} 
	 and gmt_create = #{gmtCreate} 
	 and gmt_modified = #{gmtModified} 



		order by ${sort} ${order}


		order by role_id desc



	limit #{offset}, #{limit}

select count(*) from sys_role

	 and role_id = #{roleId} 
	 and role_name = #{roleName} 
	 and role_sign = #{roleSign} 
	 and remark = #{remark} 
	 and user_id_create = #{userIdCreate} 
	 and gmt_create = #{gmtCreate} 
	 and gmt_modified = #{gmtModified}
